            self::DOCUMENTATION_LINK_WARNING_MESSAGE => [ParameterGroupFixtures::PARAMETER_GROUP_DOCUMENTATION,'DOCUMENTATION_LINK_WARNING_MESSAGE','Message de non-responsablité du contenu d\'un site externe','<p>Vous allez être redirigé vers un site web externe détenu et exploité par un tiers indépendant. Nous ne pouvons pas être tenu responsable de leur contenu. Tout accès vers ou depuis ce site externe se fera à vos propres risques.</p>',NULL,'0'],
            self::ORDER_WAITING_VALIDATE_MESSAGE => [ParameterGroupFixtures::PARAMETER_GROUP_ORDER,'ORDER_WAITING_VALIDATE_MESSAGE','Message afficher à la validation de commande par l\'adhérent','<p>LES VÊTEMENTS AUX COULEURS DU CLUB SONT EN STOCK LIMITE. VOTRE COMMANDE POURRA ÊTRE MODIFIÉE OU ANNULÉE EN FONCTION DES STOCKS RESTANTS.</p><p>Vous recevrez une notification dès traitement de votre commande.</p><p><strong>Attendez la validation avant de nous faire parvenir le règlement.</strong></p>',NULL,'1'],
