            self::CLUSTER_EXPORT_MESSAGE => [ParameterGroupFixtures::PARAMETER_GROUP_REGISTRATION,'CLUSTER_EXPORT_MESSAGE','Message à afficher pour un export de groupe','<p>Votre groupe {{ groupe }} a été validé.</p><p>Vous pouvez dès à présent télécharger les informations des participants de votre groupe.</p>',NULL,'1'],
