        ];

        foreach ($messages as $ref => [$section, $name, $label, $content, $levelType, $protected]) {
            $parameterGroupRef = ($section) ? $this->getReference($section, ParameterGroup::class) : null;
            $message = new Message();
            $message->setName($name)
                ->setLabel($label)
                ->setLevelType((int) $levelType)
                ->setContent($content)
                ->setSection($parameterGroupRef)
                ->setProtected((bool) $protected);

            $manager->persist($message);
            $this->addReference($ref, $message);
        }

        $manager->flush();
    }
